import React, { useState, useEffect } from "react";

function MusicSchedule() {
  const [schedules, setSchedules] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/api/v1/schedules")
      .then((response) => response.json())
      .then((data) => setSchedules(data))
      .catch((error) => console.error("Error fetching schedules:", error));
  }, []);

  const handleCardClick = (scheduleId) => {
    console.log(`Schedule clicked: ${scheduleId}`);
  };

  const handleButtonClick = (scheduleId) => {
    console.log(`Button clicked for schedule: ${scheduleId}`);
  };

  return (
    <div className="music-schedule-container">
      <h1>Music Festival Schedules</h1>
      {!schedules.length ? (
        <p>No schedules available at the moment.</p>
      ) : (
        <div className="card-container">
          {schedules.map((schedule) => (
            <div
              key={schedule.id}
              className="schedule-card"
              onClick={() => handleCardClick(schedule.id)}
            >
              <h2>{schedule.title}</h2>
              <p>{schedule.date}</p>
              <button
                className="view-schedule-button"
                onClick={(e) => {
                  e.stopPropagation(); // Prevent card click event from firing
                  handleButtonClick(schedule.id);
                }}
              >
                View Details
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MusicSchedule;
