import React, { useState, useEffect } from "react";

function DetailedSchedule() {
  const [schedule, setSchedule] = useState(null); // Use an object for a single schedule
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3000/api/v1/users/1/schedules/1")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => setSchedule(data))
      .catch((error) => {
        console.error("Error fetching schedule:", error);
        setError(error.message);
      });
  }, []);

  if (error) {
    return <p>Error loading schedule: {error}</p>;
  }

  if (!schedule) {
    return <p>Loading schedule...</p>;
  }

  const { schedule: details, shows, user } = schedule;

  const [imageError, setImageError] = useState(false);

  const handleImageError = () => setImageError(true); // handle image load error

  return (
    <div>
      <h1>Detailed Music Festival Schedule</h1>
      <h2>{details.title}</h2>
      <p>Date: {details.date}</p>
      <h3>Created By:</h3>
      <p>
        {user.first_name} {user.last_name}
      </p>
      <h3>Shows:</h3>
      <ul>
        {shows.map((show) => (
          <li key={show.id}>
            <h4>{show.artist}</h4>
            {/* Artist image */}
            <img
              src={`http://localhost:3000${show.artist_image_url}`}
              alt={`${show.artist} image`}
              onError={handleImageError}
              style={{
                display: imageError ? "none" : "block", // Hide if error occurs
              }}
            />
            {/* Fallback image if the artist image fails */}
            {imageError && (
              <img
                src="/images/default_artist_image.jpg"
                alt="Default Artist"
                style={{ display: "block" }}
              />
            )}

            <p>Show Details:</p>
            {/* Show image */}
            <img
              src={`http://localhost:3000${show.image_url}`}
              alt={`Show image for ${show.artist}`}
              onError={handleImageError}
              style={{
                display: imageError ? "none" : "block", // Hide if error occurs
              }}
            />
            {/* Fallback show image if the image fails */}
            {imageError && (
              <img
                src="/images/default_show_image.jpg"
                alt="Default Show"
                style={{ display: "block" }}
              />
            )}
            <p>Date: {show.date}</p>
            <p>Time: {show.time}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DetailedSchedule;
