import React, { useState, useEffect } from "react";

function DetailedSchedule() {
  const [schedule, setSchedule] = useState(null); // Use an object for a single schedule
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:3000/api/v1/users/1/schedules/1")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => setSchedule(data))
      .catch((error) => {
        console.error("Error fetching schedule:", error);
        setError(error.message);
      });
  }, []);

  if (error) {
    return <p>Error loading schedule: {error}</p>;
  }

  if (!schedule) {
    return <p>Loading schedule...</p>;
  }

  const { schedule: details, shows, users } = schedule;

  return (
    <div>
      <h1>Detailed Music Festival Schedule</h1>
      <h2>{details.title}</h2>
      <p>Date: {details.date}</p>
      <h3>Created By:</h3>
      <p>
        {users && users.length > 0 ? (
          <>
            {users[0].first_name} {users[0].last_name}
          </>
        ) : (
          "No user associated with this schedule."
        )}
      </p>
      <h3>Shows:</h3>
      <ul>
        {shows.map((show) => (
          <li key={show.id}>
            <h4>{show.artist}</h4>
            <img src={`${show.artist_image_url}`} alt={`${show.artist} image`} />
            <p>Show Details:</p>
            <img src={`${show.image_url}`} alt={`Show image for ${show.artist}`} />
            <p>Date: {show.date}</p>
            <p>Time: {show.time}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default DetailedSchedule;
