import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom"; // Import useParams hook

function DetailedSchedule() {
  const { scheduleId } = useParams(); // Get the scheduleId from the URL
  const [scheduleDetails, setScheduleDetails] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3000/api/v1/schedules/${scheduleId}`)
      .then((response) => response.json())
      .then((data) => setScheduleDetails(data))
      .catch((error) => console.error("Error fetching schedule details:", error));
  }, [scheduleId]); // Fetch details when scheduleId changes

  if (!scheduleDetails) {
    return <p>Loading...</p>;
  }

  return (
    <div className="detailed-schedule-container">
      <h1>{scheduleDetails.schedule.title}</h1>
      <p>{scheduleDetails.schedule.date}</p>
      <div className="shows-container">
        {scheduleDetails.shows.map((show) => (
          <div key={show.id} className="show-card">
            <h3>{show.artist}</h3>
            <p>{show.date} - {show.time}</p>
            <img src={show.image_url} alt={show.artist} />
            <img src={show.artist_image_url} alt={show.artist} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default DetailedSchedule;
